import React, { Component } from 'react';
import axios from 'axios';
// Just created context and they will provide : Provider and Consumer
const Context = React.createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'ADD_IMAGE':
      return {
        images: [...state.images, action.newImage]
      };

    default:
      return state;
  }
};

export class Provider extends Component {
  state = {
    groups: [
      {
        id: '',
        question: '',
        answer1: '',
        answer2: '',
        answer3: '',
        right: '',
        congrats: '',
        reaction: ''
      }
    ],
    finalVeridict: {
      id: 0,
      evaluation: ''
    },
    dispatch: action => {
      this.setState(state => reducer(state, action));
    }
  };

  async componentDidMount() {
    try {
      const response = await axios.get('http://localhost:1024/loadquiz');
      console.log(response);

      this.setState({
        groups: response.data.quiz
      });
      // console.log(this.state.groups);
    } catch (error) {
      console.log(error);
    }
  }

  async componentDidUpdate() {

  }

  render() {
    return (
      <Context.Provider value={this.state}>
        {this.props.children}
      </Context.Provider>
    );
  }
}

export const Consumer = Context.Consumer;
