import React, { Component } from 'react';

// JSX

// import propTypes from 'prop-types';
import { Consumer } from '../Context';

import TextInputGroup from '../layouts/TextInputGroup';
import Axios from 'axios';

class AddImage extends Component {
  constructor(props) {
    super(props);

    this.nameInput = React.createRef();
    this.emailInput = React.createRef();
    this.phoneInput = React.createRef();
    this.idInput = React.createRef();
  }

  state = {
    name: ' ',
    email: ' ',
    phone: ' ',
    id: 0,
    errors: {}
  };

  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleNewContact = (value, event) => {
    // event.preventDefault(); // Prevents page from reload
    const newId =
      value.persons.length !== 0
        ? value.persons[value.persons.length - 1].id + 1
        : 1;

    const { name, email, phone } = this.state;

    if (name === ' ') {
      this.setState({
        errors: { name: 'Name is required' }
      });
      return;
    }
    if (email === ' ') {
      this.setState({
        errors: { email: 'Email is required' }
      });
      return;
    }
    if (phone === ' ') {
      this.setState({
        errors: { phone: 'Phone is required' }
      });
      return;
    }
    const Person = {
      name: name,
      email: email,
      phone: phone,
      errors: {}
    };

    Axios.post(`https://jsonplaceholder.typicode.com/users/`, Person).then(
      response => {
        value.dispatch({ type: 'ADD_PERSON', newPerson: Person }); // Now it's inside the "post" action
      }
    );

    this.props.history.push('/'); // Keeping the previous page (in case the user
    // wants to go back)
  };

  render() {
    return (
      <Consumer>
        {value => (
          <div className="Add-Contact-Form">
            <TextInputGroup
              type="text"
              label="Name"
              className="name form-control"
              name="name"
              placeholder="Enter name"
              onChange={this.updateInformation}
              error={this.state.errors.name}
            />
            <TextInputGroup
              type="email"
              label="Email"
              className="email form-control"
              name="email"
              placeholder="Enter email"
              onChange={this.updateInformation}
              error={this.state.errors.email}
            />
            <small id="emailHelp" className="form-text text-muted">
              We'll never share your email with anyone else.
            </small>
            <TextInputGroup
              type="text"
              label="Phone"
              className="phone form-control"
              name="phone"
              placeholder="Enter phone"
              onChange={this.updateInformation}
              error={this.state.errors.phone}
            />{' '}
            <button
              type="submit"
              className="btn btn-primary"
              onClick={() => this.handleNewContact(value)}
            >
              Add Image{' '}
            </button>
          </div>
        )}
      </Consumer>
    );
  }
}

export default AddImage;
