import React, { Component } from 'react';
import './App.css';

import { connect } from 'react-redux';

import About from './components/pages/About';
import Header from './components/layouts/Header.js';

import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { Provider } from './Context';
import NotFound from './NotFound';
import axios from 'axios';

import Gallery from './components/Gallery';
import AddImage from './components/AddImage';

class App extends Component {
  state = {
    homeDisplay: true,
    addContactDisplay: false,
    aboutDisplay: false
  };
  /*
  state = {
    somedata: null,
    response: null
  };

  loadData = async () => {
    const result = await axios.get('http://localhost:8000/somedata');

    this.setState({
      somedata: result.data
    });
  };

  saveData = async () => {
    const result = await axios.post('http://localhost:8000/somedata', {
      data: 'Testing'
    });

    this.setState({
      response: JSON.stringify(result.data)
    });
  };
  */

  /*
    Task: Create a button "save data" that sends the post to 
    your backend and display the result

  */
  render() {
    return (
      <Provider>
        <Router>
          <div className="mt-5 App container">
            <h1 className="display-2 text-center">Image Gallery</h1>
            <Header title="Images" />
            <Switch>
              <Route exact path="/" component={Gallery} />
              <Route exact path="/addimage" component={AddImage} />
              <Route exact path="/about" component={About} />
              <Route component={NotFound} />
            </Switch>
          </div>
        </Router>
      </Provider>
    );
  }
}

export default App;
