import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../Context';
import axios from 'axios';

import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

// JSX
class Group extends Component {
  // Two variables. One have true when the user answered.
  // The second one works as a switch between right and wrong answers
  state = {
    checkedOption: 0,
    answered: false,
    passed: false
  };

  // This function
  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      checkedOption: event.target.value
    });
  };

  /* This function verifies if the user chose the right answer.
     If so, the "passed" variable is updated to "true". This way 
     the greetings are shown. If not, the notification of
     wrong answer must come out.
     Also the variable "answered" must be set to true,
     so the user can see the result of the made choice.
  */
  showResult = rightAnswer => {
    // event.preventDefault();

    // let selected = this.state.checkedOption

    if (parseInt(this.state.checkedOption) === parseInt(rightAnswer)) {
      this.setState({
        passed: true
      });
    }

    this.setState({
      answered: true
    });
  };

  render() {
    const {
      id,
      image,
      question,
      answer1,
      answer2,
      answer3,
      right,
      congrats,
      reaction
    } = this.props.group;
    /*
    let imageHeader = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between'
    };

    let buttonStyle = {
      borderRadius: '3px',
      backgroundColor: '#e33',
      color: 'white',
      width: '36px',
      height: '36px',
      boxShadow:
        '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
      fontSize: '1.0em',

      cursor: 'pointer'
    };
*/
    return (
      <Consumer>
        {content => {
          // console.log(content);
          return (
            <div className="Group">
              <div className="image-Block">
                <figure className="image-Thumb ml-4 mr-4 p-2 col-sm">
                  <img
                    className="scenario"
                    src={'/images/' + image}
                    alt=""
                    // style={{ cursor: 'pointer' }}
                  />
                </figure>
              </div>
              <Form>
                <FormGroup tag="fieldset">
                  <legend>{question}</legend>
                  <FormGroup check>
                    <Label check>
                      <Input
                        type="radio"
                        name="radio1"
                        value="1"
                        checked={this.state.checkedOption === '1'}
                        onChange={this.updateInformation}
                      />{' '}
                      {answer1}
                    </Label>
                  </FormGroup>
                  <FormGroup check>
                    <Label check>
                      <Input
                        type="radio"
                        name="radio2"
                        value="2"
                        checked={this.state.checkedOption === '2'}
                        onChange={this.updateInformation}
                      />{' '}
                      {answer2}
                    </Label>
                  </FormGroup>
                  <FormGroup check>
                    <Label check>
                      <Input
                        type="radio"
                        name="radio3"
                        value="3"
                        checked={this.state.checkedOption === '3'}
                        onChange={this.updateInformation}
                      />{' '}
                      {answer3}
                    </Label>
                  </FormGroup>
                </FormGroup>
                {this.state.answered === true ? (
                  this.state.passed === true ? (
                    <p>{congrats}</p>
                  ) : (
                    <p>{reaction}</p>
                  )
                ) : null}{' '}
                {this.state.answered === false ? (
                  <Button onClick={() => this.showResult(right)}>Submit</Button>
                ) : (
                  <Button
                    onClick={() =>
                      this.props.nextGroupHandler(this.state.passed)
                    }
                  >
                    Next
                  </Button>
                )}
              </Form>
            </div>
          );
        }}
      </Consumer>
    );
  }
}

Group.propTypes = {
  group: propTypes.object.isRequired
};

export default Group;
