import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../Context';
import axios from 'axios';

import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

// JSX
class Group extends Component {
  render() {
    const { id, image, question, answer1, answer2, answer3, right, congrats, reaction } = this.props.group;

    let imageHeader = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between'
    };

    let buttonStyle = {
      borderRadius: '3px',
      backgroundColor: '#e33',
      color: 'white',
      width: '36px',
      height: '36px',
      boxShadow:
        '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
      fontSize: '1.0em',

      cursor: 'pointer'
    };

    return (
      <Consumer>
        {content => {
          // console.log(content);
          return (
            <div className="Group">
            <div className="image-Block">
              <figure className="image-Thumb ml-4 mr-4 p-2 col-sm">
                <img
                  className="scenario"
                  src={'/images/' + image}
                  alt=""
                  // style={{ cursor: 'pointer' }}
                />
              </figure>
            </div>
            <Form>
              <FormGroup tag="fieldset">
                <legend>{question}</legend>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="radio1" data-id="1"/>{' '}
                    {answer1}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="radio2" data-id="2"/>{' '}
                    {answer2}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="radio3" data-id="3"/>{' '}
                    {answer3}
                  </Label>
                </FormGroup>
              </FormGroup>
              <Button>Submit</Button>
            </Form>
            </div>
          );
        }}
      </Consumer>
    );
  }
}

Group.propTypes = {
  group: propTypes.object.isRequired
};

export default Group;
