import React, { Component } from 'react';

import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

// JSX
class Shopping extends Component {
  // Two variables. One have true when the user answered.
  // The second one works as a switch between right and wrong answers
  state = {
    number: 0,
    price: 0,
    total: 0,
    tax: 0,
    balance: 10000
  };

  constructor(props) {
    super(props);

    this.number = React.createRef();
    this.price = React.createRef();
  }

  // This function
  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  makeCalculation = event => {
    event.preventDefault();

    let total = this.state.number * this.state.price;
    let tax = Math.round(total * 0.08, 2);
    let newBalance = this.state.balance - (total + tax);
    if (newBalance < 0) {
      alert('Out of balance! You cannot buy more phones.');
    } else {
      this.setState({
        total: total,
        tax: tax,
        balance: newBalance
      });
    }
  };

  render() {
    return (
      <div className="Shopping">
        <div>
          <Form>
            <FormGroup tag="fieldset">
              <FormGroup check>
                <Label check>
                  Number of Phones
                  <Input
                    type="number"
                    name="number"
                    onChange={this.updateInformation}
                  />{' '}
                </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  <Input
                    type="number"
                    name="price"
                    onChange={this.updateInformation}
                  />{' '}
                </Label>
              </FormGroup>{' '}
              <Button
                onClick={e => this.makeCalculation(e)}
                title="Buy Phone(s)"
              >
                Buy Phone(s)
              </Button>
            </FormGroup>
          </Form>
        </div>
      </div>
    );
  }
}

export default Shopping;
