import React, { Component } from 'react';

import axios from 'axios';

import Group from './Group';
import { Consumer } from '../Context';

import Veridict from "../pages/Veridict";

// JSX

class Quiz extends Component {

  state = {
    currentIndex: 0,
    rightAnswers: 0,
    wrongAnswers: 0,
    finalResult: 0,
    finalVeridict: {
      id: 0,
      veridict: ''
    },
    completed: false
  }
/*
  constructor(props) {
    super(props);
    this.state.currentIndex = 0;
    this.state.rightAnswers = 0;
    this.state.wrongAnswers = 0;
    this.state.finalResult = 0;

  };
*/
  /* This function handles the each group of the quiz.
      Each time the next button is pressed, the current
      Index of the question is updated. This goes until
      the last one.
   */
  handleNextBlock(passed, event){
    // event.preventDefault();
    // console.log(this.state.rightAnswers);
    if (passed) {
      let value = this.rightAnswers + 1;
      console.log(value);
      this.setState({
        rightAnswers: this.rightAnswers + 1
      });

    }
    else
      this.setState({
        currentIndex: this.currentIndex + 1
      })

  }

/* 
   The final stage. When the last question is answered and the
   user did press the nex button, this will act. It will
   calculate the percentage of success. Based on that,
   it will load the right veridict. This will be shown to the user.
*/
  async componentDidUpdate() {

    // Checking if the last question was answered
    if (this.state.currentIndex === 14){

      // Getting the result of the quiz. This will be shonw as a percentage
      let calculation = Math.round((this.stage.rightAnswers / 15) * 100, 2);
      let choice = 0; 
      // Now all the conditions to apply the right value to the choice.
      if (calculation > 0 && calculation < 25)
        choice = 1;
      else if (calculation >= 25 && calculation < 35)
        choice = 2;
      else if (calculation >= 35 && calculation < 45)
        choice = 3;
      else if (calculation >= 45 && calculation < 56,5)
        choice = 4;
      else if (calculation >= 56,5 && calculation < 73)
        choice = 5;
      else if (calculation >= 73 && calculation < 80)
        choice = 6;
      else if (calculation >= 80 && calculation < 95)
        choice = 7;
      else if (calculation >= 95 && calculation < 100)
        choice = 8;
      else 
        choice = 9;

      let difference = 15 - this.stage.rightAnswers;

      try {
        // Sending the request with the choice as the identifier for the veridict
        const response = await axios.get('http://localhost:1024/loadveridict/' + choice);
        console.log(response);

        this.setState({
          finalVeridict: response.data.evaluation,
          completed: true,
          wrongAnswers: difference,
          finalResult: calculation
        });

      } catch (error) {
        console.log(error);
      }
    }
/*    else {
      let cIndex = this.state.currentIndex + 1;
      console.log(cIndex);
      this.setState({
        currentIndex: cIndex
      });
    }
    */
  }

  render() {
    return (
      <Consumer>
        {content => {

          let groups = content.groups;
          // console.log(groups);
          return (
            <React.Fragment>
              {/* <h1 className="display-5 mb-2">
                <span className="text-danger">Contact</span> List
              </h1> */}
              <div className="main-column">
                  <Group
                    key={groups[this.state.currentIndex].id}
                    id={groups[this.state.currentIndex].id}
                    image={groups[this.state.currentIndex].image}
                    question={groups[this.state.currentIndex].question}
                    group={groups[this.state.currentIndex]}
                    nextGroupHandler={this.handleNextBlock}
                  />
              </div>
              <div className="end-page">
              {this.state.finalResult !== 0 ?
                  <Veridict key="1" id="1" rightAnswers={this.state.rightAnswers}  wrongAnswers={this.state.wrongAnswers} finalResult={this.state.finalResult} veridict={this.state.finalVeridict.veridict}/> :
                  null}
              </div>
            </React.Fragment>
          );
        }}
      </Consumer>
    );
  }
}

export default Quiz;
