import axios from 'axios';
import { AsyncSeriesHook } from 'tapable';

export const loadData = () => async dispatch => {
  const res = await axios.get('http://localhost:8000/somedata');

  console.log(res);

  dispatch({
    type: 'LOAD_DATA',
    payload: res.data
  });
};

export const incrementX = () => async dispatch => {
  dispatch({
    type: 'INCREMENT_X'
  });
};
