import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../Context';
import axios from 'axios';

// JSX
class Image extends Component {
  slideShow = id => {
    // openModal();
    // currentDiv(id);
  };
  removeImage = (id, content) => {
    try {
      axios.delete(`https://localhost:3000/:image?${id}`);
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    } catch (e) {
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    }
  };
  render() {
    const { id, image, tags } = this.props.image;

    let imageHeader = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between'
    };

    let buttonStyle = {
      borderRadius: '3px',
      backgroundColor: '#e33',
      color: 'white',
      width: '36px',
      height: '36px',
      boxShadow:
        '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
      fontSize: '1.0em',

      cursor: 'pointer'
    };

    return (
      <Consumer>
        {content => {
          return (
            <div className="card card-body mb-3">
              <figure className="image-Thumb">
                <button
                  title="Remove Image"
                  onClick={() => this.removeImage(this.props.id, content)}
                  style={buttonStyle}
                >
                  <i className="fas fa-trash-alt" />
                </button>
                <img
                  className="thumb"
                  src={image}
                  onClick={() => this.slideShow(id)}
                  style={{ cursor: 'pointer' }}
                />
              </figure>
              <div className="person-header" style={imageHeader} />
            </div>
          );
        }}
      </Consumer>
    );
  }
}

Image.propTypes = {
  image: propTypes.object.isRequired
};

export default Image;
