import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../Context';
import axios from 'axios';

import ModalCarousel from './ModalCarousel';

// JSX
class Image extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      id: 0
    };

    // toggle: this.toggle();
    this.toggle = () => this.toggle();
    this.slideShow = () => this.slideShow(this.id);
  }

  // state = {
  //   modal: false
  // };

  slideShow = id => {
    // openModal();
    // currentDiv(id);
    this.setState({
      modal: !this.state.modal
    });
    // console.log(this.state.modal);
  };

  toggle() {
    this.setState({
      modal: !this.state.modal
    });
  }
  removeImage = (id, content) => {
    try {
      axios.delete(`https://localhost:1024/:image?${id}`);
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    } catch (e) {
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    }
  };
  render() {
    // console.log(this.props.image);
    const { id, image, tags } = this.props.image;

    let imageHeader = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between'
    };

    let buttonStyle = {
      borderRadius: '3px',
      backgroundColor: '#e33',
      color: 'white',
      width: '36px',
      height: '36px',
      boxShadow:
        '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
      fontSize: '1.0em',

      cursor: 'pointer'
    };

    return (
      <Consumer>
        {content => {
          // console.log(content);
          return (
            <div className="image-Block">
              <figure className="image-Thumb ml-4 mr-4 p-2 col-sm">
                <button
                  title="Remove Image"
                  onClick={() => this.removeImage(id, content)}
                  style={buttonStyle}
                >
                  <i className="fas fa-trash-alt" />
                </button>
                <img
                  className="thumb"
                  src={'/images/' + image}
                  onClick={() => this.slideShow(id)}
                  // onClick={() => this.props.toggle()}
                  alt={tags[1]}
                  // style={{ cursor: 'pointer' }}
                />
                <figcaption className="fgcap">{tags[1]}</figcaption>
              </figure>

              <ModalCarousel
                stateValue={this.state.modal}
                className="modal-slideshow"
                // isOpen={this.state.modal}
                // toggle={this.toggle}
                // external={externalCloseBtn}
              />
            </div>
          );
        }}
      </Consumer>
    );
  }
}

Image.propTypes = {
  image: propTypes.object.isRequired
};

export default Image;
