import React, { Component } from 'react';
import axios from 'axios';
// import reducer from './reducer';

// Just created context and they will provide : Provider and Consumer
const Context = React.createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'NEXT_QUESTION':
      if (parseInt(action.currentIndex) < 14) {
        if (action.passed === true)
          return {
            passed: false,
            answered: false,
            option: '',
            currentIndex: state.currentIndex + 1
          };
        else
          return {
            answered: false,
            option: '',
            currentIndex: state.currentIndex + 1
          };
      }
      break;
    case 'CHECK_ANSWER':
      if (parseInt(action.chosenOpt) === parseInt(action.theRightOne))
        return {
          passed: true,
          answered: true,
          rightAnswers: state.rightAnswers + 1,
          option: action.chosenOpt
        };
      else
        return {
          answered: true,
          wrongAnswers: state.wrongAnswers + 1,
          option: action.chosenOpt
        };
    default:
      return state;
  }
};

export class Provider extends Component {
  state = {
    groups: [
      {
        id: '',
        question: '',
        answer1: '',
        answer2: '',
        answer3: '',
        right: '',
        congrats: '',
        reaction: ''
      }
    ],
    option: '',
    currentIndex: 0,
    rightAnswers: 0,
    wrongAnswers: 0,
    finalResult: 0,
    finalVeridict: {
      id: 0,
      veridict: ''
    },
    passed: false,
    answered: false,
    completed: false,
    dispatch: action => {
      this.setState(state => reducer(state, action));
    }
  };

  async componentDidMount() {
    try {
      const response = await axios.get('http://localhost:1024/loadquiz');
      console.log(response);

      this.setState({
        groups: response.data.quiz
      });
      // console.log(this.state.groups);
    } catch (error) {
      console.log(error);
    }
  }

  /* 
   When the last question is answered and the
   user did press the nex button, this will act. It will
   calculate the percentage of success. Based on that,
   it will load the right veridict. This will be shown to the user.
*/
  async componentWillUpdate() {
    // Checking if the last question was answered
    if (this.state.currentIndex === 15) {
      // Getting the result of the quiz. This will be shonw as a percentage
      let calculation = Math.round((this.state.rightAnswers / 15) * 100, 2);
      let choice = 0;
      // Now all the conditions to apply the right value to the choice.
      if (calculation > 0 && calculation < 25) choice = 1;
      else if (calculation >= 25 && calculation < 35) choice = 2;
      else if (calculation >= 35 && calculation < 45) choice = 3;
      else if ((calculation >= 45 && calculation < 56, 5)) choice = 4;
      else if ((calculation >= 56, 5 && calculation < 73)) choice = 5;
      else if (calculation >= 73 && calculation < 80) choice = 6;
      else if (calculation >= 80 && calculation < 95) choice = 7;
      else if (calculation >= 95 && calculation < 100) choice = 8;
      else choice = 9;

      // let difference = 15 - this.state.rightAnswers;

      try {
        // Sending the request with the choice as the identifier for the veridict
        const response = await axios.get(
          'http://localhost:1024/loadveridict/' + choice
        );
        console.log(response);

        this.setState({
          finalVeridict: response.data.evaluation,
          completed: true,
          // wrongAnswers: difference,
          finalResult: calculation
        });
      } catch (error) {
        console.log(error);
      }
    }
    /* else {
      let cIndex = this.state.currentIndex + 1;
      console.log(cIndex);
      this.setState({
        currentIndex: cIndex
      });
    }*/
  }

  render() {
    return (
      <Context.Provider value={this.state}>
        {this.props.children}
      </Context.Provider>
    );
  }
}

export const Consumer = Context.Consumer;
