import React, { Component } from 'react';

import { Button, Form, FormGroup, Label, Input } from 'reactstrap';

// JSX
class Shopping extends Component {
  // Two variables. One have true when the user answered.
  // The second one works as a switch between right and wrong answers
  state = {
    number: 0,
    price: 0,
    totalPhones: 0,
    total: 0,
    tax: 0,
    totalVat: 0,
    balance: 10000
  };

  constructor(props) {
    super(props);

    this.number = React.createRef();
    this.price = React.createRef();
  }

  // This function
  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  makeCalculation = event => {
    event.preventDefault();

    let total = this.state.total + this.state.number * this.state.price;
    let tax = Math.round(total * 0.23, 2);
    let newBalance = this.state.balance - (total + tax);
    if (newBalance < 0) {
      alert('Out of balance! You cannot buy more phones.');
    } else {
      this.setState({
        totalPhones: this.state.totalPhones + this.state.number,
        total: total,
        tax: tax,
        totalVat: total + tax,
        balance: newBalance
      });
    }
  };

  render() {
    return (
      <div className="Shopping">
        <div>
          <Form>
            <FormGroup tag="fieldset">
              <FormGroup check>
                <Label check>
                  Number of Phones
                  <Input
                    type="number"
                    name="number"
                    onChange={this.updateInformation}
                  />{' '}
                </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  Price
                  <Input
                    type="number"
                    name="price"
                    onChange={this.updateInformation}
                  />{' '}
                </Label>
              </FormGroup>{' '}
              <br />
              <FormGroup check>
                <Button
                  onClick={e => this.makeCalculation(e)}
                  title="Buy Phone(s)"
                >
                  Buy Phone(s)
                </Button>
              </FormGroup>
            </FormGroup>
          </Form>
        </div>
        <div className="Status">
          <br />
          <p>Number of Phones: {this.state.totalPhones}</p>
          <p>Total Spent: {this.state.total} €</p>

          <p>Tax Rate: 23%</p>

          <p>VAT: {this.state.tax} €</p>

          <p>Total (including taxes): {this.state.totalVat} €</p>

          <p>Account Balance: {this.state.balance}</p>
        </div>
      </div>
    );
  }
}

export default Shopping;
