import React, { Component } from 'react';
import './App.css';

import { connect } from 'react-redux';
import axios from 'axios';

// import { loadData } from './actions';

class App extends Component {
  state = {
    somedata: null,
    response: null
  };

  loadData = async () => {
    const result = await axios.get('http://localhost:8000/somedata');

    this.setState({
      somedata: result.data
    });
  };

  saveData = async () => {
    const result = await axios.post('http://localhost:8000/somedata');

    this.setState({
      response: result.somedata
    });
  };

  /*
    Task: Create a button "save data" that sends the post to 
    your backend and display the result

  */
  render() {
    return (
      <div className="App">
        <h1>Axios and Backend</h1>
        <button onClick={this.loadData}>Load Data</button>
        <div>
          {this.state.somedata && (
            <h2>Data arrived - {this.state.somedata.result}</h2>
          )}
        </div>
        <button onClick={this.saveData}>Save Data</button>
        <div>
          {this.state.response && (
            <h2>Reponse - {this.state.response.result}</h2>
          )}
        </div>
      </div>
    );
  }
}

export default App;
