import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../../Context';
import EditContact from '../contacts/EditContact';
import axios from 'axios';

// JSX
class Contact extends Component {
  state = {
    userInfo: false,
    editForm: false
  };

  // This function changes the value of the boolean variable userInfo
  showClick = () => {
    console.log('React...');
    this.setState({
      userInfo: !this.state.userInfo
    });
  };

  removeImage = (id, content) => {
    /* content.dispatch({
      type: 'REMOVE_PERSON',
      id: id
    });

    Bellow the new version
    */
    try {
      axios.delete(`https://jsonplaceholder.typicode.com/users/${id}`);
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    } catch (e) {
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    }
  };
  render() {
    const { name, email, phone } = this.props.contact;

    let personHeader = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between'
    };

    let buttonStyle = {
      borderRadius: '3px',
      backgroundColor: '#e33',
      color: 'white',
      width: '36px',
      height: '36px',
      boxShadow:
        '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
      fontSize: '1.0em',

      cursor: 'pointer'
    };

    let mailTo = 'mailto:' + JSON.stringify(email);

    let showInfo = this.state.userInfo;

    let showEditForm = this.state.editForm;

    return (
      <Consumer>
        {content => {
          return (
            <div className="card card-body mb-3">
              <div className="person-header" style={personHeader}>
                <div className="user-name">
                  <h4>
                    {name}{' '}
                    <i
                      className="fas fa-sort-down"
                      onClick={() => this.showClick()}
                      style={{ cursor: 'pointer' }}
                    />
                  </h4>
                </div>
                <div className="buttons">
                  <button
                    title="Edit Contact"
                    onClick={() => this.showEditContact()}
                    style={buttonStyle}
                  >
                    <i className="fas fa-user-edit" />
                  </button>
                  <button
                    title="Remove Person"
                    onClick={() => this.removePerson(this.props.id, content)}
                    style={buttonStyle}
                  >
                    <i className="fas fa-trash-alt" />
                  </button>
                </div>
              </div>
              {showInfo ? (
                <ul>
                  <li className="list-group-item">
                    Email: <a href={mailTo}>{email}</a>
                  </li>
                  <li className="list-group-item">Phone: {phone}</li>
                </ul>
              ) : null}
              {showEditForm ? (
                <EditContact
                  id={this.props.id}
                  name={name}
                  email={email}
                  phone={phone}
                />
              ) : null}
            </div>
          );
        }}
      </Consumer>
    );
  }
}

Contact.propTypes = {
  contact: propTypes.object.isRequired
};

export default Contact;
