import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../Context';
import axios from 'axios';

import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

// JSX
class Shopping extends Component {
  // Two variables. One have true when the user answered.
  // The second one works as a switch between right and wrong answers
  state = {
    checkedOption: 0,
    answered: false,
    passed: false
  };

  constructor(props) {
    super(props);

    this.nameInput = React.createRef();
    this.priceInput = React.createRef();
  }

  // This function
  updateInformation = event => {
    event.preventDefault(); // Prevents page from reload
    // if (!element.empty && element.complete)
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    return (
      <div className="Shopping">
        <div>
          <Form>
            <FormGroup tag="fieldset">
              <FormGroup check>
                <Label check>
                  <Input
                    type="text"
                    name="name"
                    onChange={this.updateInformation}
                  />{' '}
                </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  <Input
                    type="text"
                    name="price"
                    onChange={this.updateInformation}
                  />{' '}
                </Label>
              </FormGroup>
            </FormGroup>
          </Form>
        </div>
      </div>
    );
  }
}
/*
Group.propTypes = {
  group: propTypes.object.isRequired
};
*/
export default Shopping;
