import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  render() {
    const name = 'Larry';

    const a = 6;

    const b = 7;

    const showHalloWorld = false;

    const num1 = 42;
    const num2 = 62;

    let math = <h4>24+23 = {24 + 23}</h4>;

    let math2 = (
      <h4>
        {num1} + {num2} = {num1 + num2}
      </h4>
    );

    let hwString = 'Hallow again';

    let header1 = <h1>{hwString}</h1>;

    return (
      <div className="App">
        <h1>The App Component</h1>
        <h2>Hello {name}</h2>
        <h2>Hello {name.toLocaleUpperCase()}</h2>
        <h3>1+1 = {1 + 1}</h3>
        <h3>a+b = {a + b}</h3>
        <h3>Nothing: {null}</h3>
        {showHalloWorld ? <h3>Hallo World</h3> : null}
        {math}
        {math2}
        {/* Task: Create a variable that contains "Hallo World". It displays
                  the string inside a h1 tag that you save in a vairable. */}

        {header1}
      </div>
    );
  }
}

export default App;
