import React, { Component } from 'react';
import propTypes from 'prop-types';
import { Consumer } from '../Context';
import axios from 'axios';

import ModalCarousel from '../components/ModalCarousel';

// JSX
class Image extends Component {
  state = {
    modalCarousel: false
  };

  slideShow = id => {
    // openModal();
    // currentDiv(id);
    this.setState({
      modalCarousel: !this.state.modalCarousel
    });
    // console.log(this.state.modalCarousel);
  };
  removeImage = (id, content) => {
    try {
      axios.delete(`https://localhost:1024/:image?${id}`);
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    } catch (e) {
      content.dispatch({
        type: 'REMOVE_IMAGE',
        id: id
      });
    }
  };
  render() {
    // console.log(this.props.image);
    const { id, image, tags } = this.props.image;

    let imageHeader = {
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between'
    };

    let buttonStyle = {
      borderRadius: '3px',
      backgroundColor: '#e33',
      color: 'white',
      width: '36px',
      height: '36px',
      boxShadow:
        '0 4px 8px 0 rgba(128, 98, 31, 0.2), 0 6px 20px 0 rgba(128, 98, 31, 0.19)',
      fontSize: '1.0em',

      cursor: 'pointer'
    };

    return (
      <Consumer>
        {content => {
          // console.log(content);
          return (
            <div className="image-Block">
              <figure className="image-Thumb ml-4 mr-4 p-2 col-sm">
                <button
                  title="Remove Image"
                  onClick={() => this.removeImage(id, content)}
                  style={buttonStyle}
                >
                  <i className="fas fa-trash-alt" />
                </button>
                <img
                  className="thumb"
                  src={'/images/' + image}
                  onClick={() => this.slideShow(id)}
                  alt={tags[1]}
                  // style={{ cursor: 'pointer' }}
                />
                <figcaption className="fgcap">{tags[1]}</figcaption>
              </figure>
              {this.state.modalCarousel ? (
                <ModalCarousel isOpen={this.state.modalCarousel} />
              ) : null}
            </div>
          );
        }}
      </Consumer>
    );
  }
}

Image.propTypes = {
  image: propTypes.object.isRequired
};

export default Image;
