        {content => {

          let groups = content.groups;
          // console.log(content);
          return (
            <React.Fragment>
              {/* <h1 className="display-5 mb-2">
                <span className="text-danger">Contact</span> List
              </h1> 
              // content.groups.map(group => (
              */}
              <div className="main-column">


                  <Group
                    key={content.groups[1].id}
                    id={groups[1].id}
                    image={groups[1].image}
                    question={groups[1].question}
                    group={groups[1]}
                  />

              </div>
            </React.Fragment>
          );
        }}
      </Consumer>
    );
  }
}

export default Quiz;
